class Demo extends React.Component {

    constructor(props) {
        super(props);
        this.state = {count:20, mode:"Pause"};
    }

    compte() {
        this.setState({count: this.state.count - 1})
    }

    timer() {
        setInterval(this.compte.bind(this), 1000);
    }

    render() {
        return (
            <div style={styles}>
                <h1>- POMODORO -</h1>
                <div>
                    <button onClick={this.timer.bind(this)}> Lancer un timer </button>
                </div>
                <p>{this.state.mode}</p>
                <p>Timer :  {this.state.count} {this.state.mode}</p>
            </div>
        )
    }
}

let count = 1;
let text = "welcome 2";
const rootElt = document.getElementById("root");
render (<Demo count={count} text={text}/>, rootElt);
