import React from 'react';
import {render} from 'react-dom';

let bgColor_timer = "white";
const hauteurTitre = {
    height : "7vh",
    backgroundColor : "#eea",
    paddingTop : "10px",
    paddingBottom : "35px",
    display : "flex",
    flexDirection : "row"
}
const title = {
    color : "black",
    paddingLeft : "16px",
    fontFamily : "Lato"
}

const hauteurContenu = {
    height : "93vh",
    backgroundColor : "#eea",
    align : "center",
    textAlign: "center",
    padding :"20px"
}

const timer = {
    backgroundColor : bgColor_timer,
    width : "120px",
    margin : "auto",
    padding : "15px",
    fontSize : "30px",
}

const btn_timer = {
    backgroundColor : "white",
    border : "none",
    borderRadius : "18px",
    boxShadow : "6px 0 18px rgb(0 0 0 / 6%)",
    padding : "14px",
    cursor : "pointer",
}


let stopTimerStatus = 0;
let nbTimerEnclenches = 1;

// 1 -> mode travail ; 2 -> mode pause; 3 -> mode travail; 4 -> mode pause

class Demo extends React.Component {

    constructor(props) {
        super(props);
        this.state = {count:25, mode:"MODE PAUSE : Have break, have a KitKat ! ", red:"", temps :"00:00"};
    }

    componentDidMount(){
        let tempsRestants = this.affichageTemps();
        this.state.temps = tempsRestants;
    }

    affichageTemps () {
        // console.log(nbTimerEnclenches);
        let minutes = Math.floor(this.state.count / 60);
        let secondes = this.state.count - minutes * 60;

        function str_pad_left(string,pad,length) {
            return (new Array(length+1).join(pad)+string).slice(-length);
        }

        let tempsRestant = str_pad_left(minutes,'0',2)+':'+str_pad_left(secondes,'0',2);
        // console.log(tempsRestant);
        this.state.temps = tempsRestant;
    }

    compte() {
        // console.log(this.state.count);
        this.affichageTemps();
        if (nbTimerEnclenches % 2 === 0) {
            console.log("tour divisable par 2");
            this.state.mode = "MODE PAUSE : Have break, have a KitKat ! "
        }
        else {
            this.state.mode = "MODE TRAVAIL : Au boulot !"
        }

        if (stopTimerStatus === 0) {
            this.setState({count: this.state.count - 1})

            if (this.state.count < 20 && this.state.count !== 0 && this.state.count > 0) {
                document.getElementById("timer").style.backgroundColor = "#f43";
                this.state.red = "Plus que 20 secondes !";
            }
            else if (this.state.count < 0) {
                //this.state.mode = "MODE PAUSE : Have break, have a KitKat ! "
                this.state.red ="";
                document.getElementById("timer").style.backgroundColor = "white";
                nbTimerEnclenches++;
                stopTimerStatus = 1;
            }
        }
        else if (stopTimerStatus === 1) {
            this.state.count = 25;
            stopTimerStatus = 0;
        }
    }

    timer() {
        setInterval(this.compte.bind(this), 1000);
    }

    render() {
        return (
            <div>
                <div style={hauteurTitre}>
                    <h1 style={title}> MON PODORO </h1>
                    <p style={title} >REACT - PROJET NÂ°1 </p>
                </div>
                <div style={hauteurContenu}>
                    <div>
                        <button style={btn_timer} onClick={this.timer.bind(this)}> Lancer un timer </button>
                    </div>
                    <p>{this.state.mode}</p>
                    <p>{this.state.red}</p>
                    <p id ="timer" style={timer}>{this.state.temps}</p>
                </div>
            </div>
        )
    }
}

let count = 1;
let text = "welcome 2";
const rootElt = document.getElementById("root");
render (<Demo count={count} text={text}/>, rootElt);
